// Logo ve İmza tercihi: Base64 vs Link
function get_company_logo() {
    $base64_logo = 'data:image/png;base64,'; // Buraya logonuzun base64 kodunu ekleyin
    $logo_url = 'https://www.bi-siparis.com/wp-content/uploads/2023/08/logo.png';
    
    if (!empty($base64_logo) && strlen($base64_logo) > 100) {
        return $base64_logo;
    }
    return $logo_url;
}

function get_company_signature() {
    $base64_signature = 'data:image/png;base64,'; // Buraya imzanızın base64 kodunu ekleyin
    $signature_url = 'https://www.bi-siparis.com/wp-content/uploads/2023/08/signature.png';
    
    if (!empty($base64_signature) && strlen($base64_signature) > 100) {
        return $base64_signature;
    }
    return $signature_url;
}

