// 1. Basit JWT Token sistemi
function generate_warranty_token($order_id, $customer_email) {
    $payload = array(
        'order_id' => $order_id,
        'email' => $customer_email,
        'timestamp' => time(),
        'expire' => time() + (24 * 60 * 60) // 24 saat
    );
    
    $header = json_encode(['typ' => 'JWT', 'alg' => 'HS256']);
    $payload = json_encode($payload);
    
    $base64Header = str_replace(['+', '/', '='], ['-', '_', ''], base64_encode($header));
    $base64Payload = str_replace(['+', '/', '='], ['-', '_', ''], base64_encode($payload));
    
    $signature = hash_hmac('sha256', $base64Header . "." . $base64Payload, 'warranty_secret_key_' . SECURE_AUTH_KEY, true);
    $base64Signature = str_replace(['+', '/', '='], ['-', '_', ''], base64_encode($signature));
    
    return $base64Header . "." . $base64Payload . "." . $base64Signature;
}

function verify_warranty_token($token) {
    $parts = explode('.', $token);
    if (count($parts) !== 3) {
        return false;
    }
    
    list($base64Header, $base64Payload, $base64Signature) = $parts;
    
    // Signature doğrula
    $signature = hash_hmac('sha256', $base64Header . "." . $base64Payload, 'warranty_secret_key_' . SECURE_AUTH_KEY, true);
    $expectedSignature = str_replace(['+', '/', '='], ['-', '_', ''], base64_encode($signature));
    
    if (!hash_equals($base64Signature, $expectedSignature)) {
        return false;
    }
    
    // Payload decode
    $payload = json_decode(base64_decode($base64Payload), true);
    
    // Expiry check
    if ($payload['expire'] < time()) {
        return false;
    }
    
    return $payload;
}

// 2. Rate limiting
function check_rate_limit($max_requests = 10, $time_window = 300) {
    $ip = $_SERVER['REMOTE_ADDR'];
    $key = 'rate_limit_' . md5($ip);
    
    $requests = get_transient($key);
    if ($requests === false) {
        set_transient($key, 1, $time_window);
        return true;
    }
    
    if ($requests >= $max_requests) {
        return false;
    }
    
    set_transient($key, $requests + 1, $time_window);
    return true;
}

// 3. Activity logging
function log_warranty_activity($order_id, $action, $details = '') {
    global $wpdb;
    
    $table_name = $wpdb->prefix . 'warranty_logs';
    
    // Tablo yoksa oluştur
    if ($wpdb->get_var("SHOW TABLES LIKE '$table_name'") != $table_name) {
        $charset_collate = $wpdb->get_charset_collate();
        $sql = "CREATE TABLE $table_name (
            id mediumint(9) NOT NULL AUTO_INCREMENT,
            order_id mediumint(9) NOT NULL,
            action varchar(50) NOT NULL,
            ip_address varchar(45) NOT NULL,
            user_agent text,
            details text,
            created_at datetime DEFAULT CURRENT_TIMESTAMP,
            PRIMARY KEY (id),
            KEY order_id (order_id),
            KEY action (action),
            KEY created_at (created_at)
        ) $charset_collate;";
        
        require_once(ABSPATH . 'wp-admin/includes/upgrade.php');
        dbDelta($sql);
    }
    
    $wpdb->insert(
        $table_name,
        array(
            'order_id' => $order_id,
            'action' => $action,
            'ip_address' => $_SERVER['REMOTE_ADDR'],
            'user_agent' => $_SERVER['HTTP_USER_AGENT'],
            'details' => $details
        )
    );
}

