        <script>
            window.onload = function() {
                // QR kod hata kontrolü
                var qrImages = document.querySelectorAll('.qr-code img');
                qrImages.forEach(function(img) {
                    img.addEventListener('error', function() {
                        console.log('QR kod yüklenemedi, fallback gösteriliyor');
                        this.style.display = 'none';
                        var fallback = this.nextElementSibling;
                        if (fallback) {
                            fallback.style.display = 'block';
                        }
                    });
                    
                    // Timeout ile backup kontrol
                    setTimeout(function() {
                        if (img.naturalWidth === 0) {
                            img.style.display = 'none';
                            var fallback = img.nextElementSibling;
                            if (fallback) {
                                fallback.style.display = 'block';
                            }
                        }
                    }, 3000);
                });
                
                // Print dialog
                setTimeout(function() {
                    window.print();
                }, 500);
            };
            
            function printWarranty() {
                window.print();
            }
            
            // JavaScript QR kod oluşturma (backup plan)
            function generateBackupQR(text, elementId) {
                // Basit QR kod alternatifi
                var qrContainer = document.getElementById(elementId);
                if (qrContainer) {
                    qrContainer.innerHTML = '<a href="' + text + '" target="_blank" class="qr-fallback">🔍 Online<br>Doğrula</a>';
                }
            }
        </script>
